  ggplot(aes(y = Item, x = n)) +
  geom_col() +
  scale_fill_continuous(palette = "Spectral") +
  scale_y_discrete(label = ~ str_replace(.x , "\\(.*\\)", "") |&gt; str_wrap(width = 40) ) +
  labs(x = "Countries")

