2.1 Day coverage

tuesdata$cranes |&gt;
  filter(!is.na(observations)) |&gt;
  mutate(year = year(date), month = month(date)) |&gt;
  mutate(date_simple = date(str_c('2025-', month, '-', day(date)))) |&gt;
  ggTimeSeries::ggplot_calendar_heatmap(
    'date',
    'observations',
    monthBorderSize = 0.5,
    dayBorderColour = 'white',
