get_density &lt;- function(x, y, ...) {
  # Calculate the 2D kernel density estimate
  dens &lt;- MASS::kde2d(x, y, ...)

  # Find the intervals for each point in x and y
  ix &lt;- findInterval(x, dens$x)
  iy &lt;- findInterval(y, dens$y)

  # Combine the indices
  ii &lt;- cbind(ix, iy)

  # Return the density value for each point
  return(dens$z[ii])
}

