3 Transform Data for Plotting

data2plot &lt;- 
  bl_funding |&gt;
  select(year, ends_with('y2000_gbp_millions')) |&gt;
  pivot_longer(-year, names_to = 'type', values_to = 'amount_end') |&gt;
  mutate(type = str_remove(type, '_y2000_gbp_millions'))

data2plot &lt;- 
  left_join(
    data2plot,
    data2plot |&gt; transmute(year = year - 1, type, amount_start = amount_end),
    by = c('year', 'type'))

data2plot &lt;- 
  data2plot |&gt;
  mutate(change = if_else(amount_start-amount_end &gt; 0, 'Increase', 'Decrease')) |&gt;
  filter(type == 'total') |&gt;
  mutate(amount_start = if_else(year == max(year), 0, amount_start)) |&gt;
  mutate(amount_end = if_else(year == min(year), 0, amount_end)) |&gt;
  mutate(change = if_else(year %in% c(min(year), max(year)), "total", change))

3 Transform Data for Plotting

data2plot &lt;- 
  weekly_gas_prices |&gt;
  filter(date &gt; as.Date('2000-01-01')) |&gt;
  mutate(month = month(date)) |&gt;
  mutate(year = year(date)) |&gt;
  filter(fuel == 'gasoline') |&gt;
  group_by(fuel, grade, formulation, month, year) |&gt;
  mutate(price = mean(price, na.rm = TRUE)) |&gt;
  filter(grade != "all", formulation != "all") |&gt;
  ungroup() |&gt;
  pivot_wider(names_from = grade, values_from = price) |&gt;
  mutate(delta_premium = premium - midgrade, delta_regular = regular - midgrade) |&gt;
  pivot_longer(cols = c(delta_premium, delta_regular), names_to = 'type', values_to = 'price')

